mapper.insert:

		SELECT CONCAT('CONT', '_', (SELECT LPAD(COUNT(*)+1,4,'0') FROM TB_CONTRACT ))

	INSERT INTO TB_CONTRACT(
		contract_cd
		,contract_start_date
		,contract_end_date
		,client_cd
		,contract_amount 
		,contract_contents
		,contract_type
		,contract_registration_date
		,contract_registrant
		,license_kindergarten)
	VALUES(
		#{contractCd}
		,#{contractStartDate}
		,#{contractEndDate}
		,#{clientCd}
		,#{contractAmount}
		,#{contractContents}
		,#{contractType}
		,#{contractRegistrationDate}
		,#{contractRegistrant}
		,#{licenseKindergarten})

mapper.update:
UPDATE TB_CONTRACT
SET			
	contract_start_date = #{contractStartDate}
	,contract_end_date = #{contractEndDate}
	,cleint_cd = #{clientCd}
	,contract_amount = #{contractAmount}
	,contract_contents = #{contractContents}
	,contract_type = #{contractType}
	,contract_registrant = #{contractRegistrant}
WHERE contract_cd = #{contractCd}
