mapper.insert:


		SELECT CONCAT('KIDS', '_', (SELECT LPAD(COUNT(*)+1,4,'0') FROM TB_KIDS ))

INSERT INTO hanboa.TB_KIDS (
	kids_cd
	,kids_nm
	,kids_date_of_birth
	,kids_address
	,kids_parent_phone
	,kids_spare_phone
	,kids_gender
	,kids_commuting_type
	,kids_caution
	,kids_admission
	,license_kindergarten
)
VALUES (
	#{kidsCd}
	,#{kidsNm}
	,#{kidsDateOfBirth}
	,#{kidsAddress}
	,#{kidsParentPhone}
	,#{kidsSparePhone}
	,#{kidsGender}
	,#{kidsCommutingType}
	,#{kidsCaution}
	,CURDATE()
	,#{licenseKindergarten}
)

mapper.update:
UPDATE hanboa.TB_KIDS
SET kids_nm					= #{kidsNm}
	,kids_date_of_birth		= #{kidsDateOfBirth}
	,kids_address			= #{kidsAddress}
	,kids_parent_phone		= #{kidsParentPhone}
	,kids_spare_phone		= #{kidsSparePhone}
	,kids_gender			= #{kidsGender}
	,kids_commuting_type	= #{kidsCommutingType}
	,kids_caution			= #{kidsCaution}
WHERE kids_cd = #{kidsCd}
